package net.hedtech.banner.aip

import grails.test.*
import org.junit.Test
class ActionItemMaintenanceReadOnlyTests extends GrailsUnitTestCase {
 	def obj

protected void setUp() {
	super.setUp()
}


@Test
void testNullable()  { 
	ActionItemMaintenanceReadOnly obj = new ActionItemMaintenanceReadOnly();
	mockDomain(ActionItemMaintenanceReadOnly, [obj])
	obj.actionItemId= null 
	obj.actionItemName= null 
	obj.actionItemFolderId= null 
	obj.actionItemFolderId1= null 
	obj.actionItemStatus= null 
	obj.lastModified= null 
	obj.lastModifiedBy= null 

	obj.save() 
 
	assertTrue "nullable" == obj.errors['actionItemId'] 
	assertTrue "nullable" == obj.errors['actionItemName'] 
	assertTrue "nullable" == obj.errors['actionItemFolderId'] 
	assertTrue "nullable" == obj.errors['actionItemFolderId1'] 
	assertTrue "nullable" == obj.errors['actionItemStatus'] 
	assertTrue "nullable" == obj.errors['lastModified'] 
	assertTrue "nullable" == obj.errors['lastModifiedBy'] 
} 


@Test
void testMaxSize()  { 
	ActionItemMaintenanceReadOnly obj = new ActionItemMaintenanceReadOnly();
	mockDomain(ActionItemMaintenanceReadOnly, [obj])
	obj.actionItemId= 0
	obj.actionItemName= "A"
	obj.actionItemName= ""
	obj.actionItemFolderId= 0
	obj.actionItemFolderId1= "A"
	obj.actionItemFolderId1= ""
	obj.actionItemStatus= "A"
	obj.actionItemStatus= "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"
	obj.lastModified= new Date()
	obj.lastModifiedBy= "A"
	obj.lastModifiedBy= "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"
	obj.actionItemCreator_id= "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"
	obj.lastModifiedBy2= "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"
	obj.actionItemTemplateName= ""
	obj.actionItemPageId= "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"

	obj.save() 
 
	assertTrue "maxSize" == obj.errors['actionItemName']
	assertTrue "maxSize" == obj.errors['actionItemFolderId1']
	assertTrue "maxSize" == obj.errors['actionItemStatus']
	assertTrue "maxSize" == obj.errors['lastModifiedBy']
	assertTrue "maxSize" == obj.errors['actionItemCreator_id']
	assertTrue "maxSize" == obj.errors['lastModifiedBy2']
	assertTrue "maxSize" == obj.errors['actionItemTemplateName']
	assertTrue "maxSize" == obj.errors['actionItemPageId']
}

